import { useState } from 'react';
 
const AddNewItem = ({ items, setItems }) => {
  const [itemName, setItemName] = useState('');
  const [itemPrice, setItemPrice] = useState('');
  const [itemQuantity, setItemQuantity] = useState('');
  const [errors, setErrors] = useState({});
 
  const validateFields = () => {
    const newErrors = {};
    if (!itemName) newErrors.itemName = 'Item Name is required';
    if (!itemPrice || isNaN(parseFloat(itemPrice)) || parseFloat(itemPrice) <= 0)
      newErrors.itemPrice = 'Valid Item Price is required';
    if (!itemQuantity || isNaN(parseInt(itemQuantity, 10)) || parseInt(itemQuantity, 10) <= 0)
      newErrors.itemQuantity = 'Valid Quantity is required';
 
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
 
  const handleAddNewItem = () => {
    if (!validateFields()) return;
 
    const newItem = {
      name: itemName,
      price: parseFloat(itemPrice),
      inStock: parseInt(itemQuantity, 10),
    };
 
    // API call to add new item to the backend
    fetch('http://localhost:8765/PRODUCT-SERVICE/product/create', 
        {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(newItem),
    })
      .then((response) => response.json())
      .then((data) => {
        setItems([...items, data]);
        console.log('New item added:', data);
 
        // Reset form fields
        setItemName('');
        setItemPrice('');
        setItemQuantity('');
        setErrors({});
      })
      .catch((error) => console.error('Failed to add new item:', error));
  };
 
  return (
<div className="add-new-item-card">
<h2>Add Item to Stock</h2>
<div className="form-field">
<label>Item Name:</label>
<input
          type="text"
          value={itemName}
          onChange={(e) => setItemName(e.target.value)}
          style={{ borderColor: errors.itemName ? 'red' : '' }}
        />
</div>
      {errors.itemName && <span style={{ color: 'red' }}>{errors.itemName}</span>}
<div className="form-field">
<label>Item Price (Â£):</label>
<input
          type="number"
          step="0.01"
          value={itemPrice}
          onChange={(e) => setItemPrice(e.target.value)}
          style={{ borderColor: errors.itemPrice ? 'red' : '' }}
        />
</div>
      {errors.itemPrice && <span style={{ color: 'red' }}>{errors.itemPrice}</span>}
<div className="form-field">
<label>Quantity to Add:</label>
<input
          type="number"
          value={quantity}
          onChange={(e) => setItemQuantity(e.target.value)}
          style={{ borderColor: errors.quantity ? 'red' : '' }}
        />
</div>
      {errors.quantity && <span style={{ color: 'red' }}>{errors.itemQuantity}</span>}
<button onClick={handleAddNewItem} className="add-item-button">
        Add Item
</button>
</div>
  );
};
 
export default AddNewItem;
